<template>
    <span class="flag-icon" :class="[flagIconClass, flagIconSize]">
    </span>
</template>

<script>
  export default {
    props: ['iso', 'size'],
    computed: {
      flagIconClass () {
        return `flag-icon-${this.iso.toLowerCase()}`
      },
      flagIconSize () {
        if (this.size == null) {
          return 'flag-icon-1'
        } else {
          return `flag-icon-${this.size}`
        }
      }
    }
  }
</script>

